// src/pages/admin/Dashboard.jsx
import { useEffect, useState, useCallback } from "react";
import { getAdminDashboard, deleteEntity } from "../../services/adminApi";
import { useConfirm } from "../../hooks/useConfirm";
import CrudModal from "../../components/admin/CrudModal";

const EMPTY_LATEST = {
  users: [],
  categories: [],
  products: [],
  customers: [],
  orders: [],
  employees: [],
  sales: [],
  reservations: [],
};

const Dashboard = () => {
  // ===================== STATES =====================
  const [loading, setLoading] = useState(true);

  const [stats, setStats] = useState({
    users: 0,
    categories: 0,
    products: 0,
    customers: 0,
    orders: 0,
    employees: 0,
    sales: 0,
    reservations: 0,
  });

  const [latest, setLatest] = useState(EMPTY_LATEST);

  const [modalOpen, setModalOpen] = useState(false);
  const [currentTable, setCurrentTable] = useState("");
  const [formData, setFormData] = useState({});

  const { confirm, ConfirmModal } = useConfirm();

  // ===================== ICONS =====================
  const iconMap = {
    users: "üë•",
    categories: "üìÅ",
    products: "üì¶",
    customers: "üë§",
    orders: "üõí",
    employees: "üíº",
    sales: "üìà",
    reservations: "üìÖ",
  };

  // ===================== FETCH DASHBOARD =====================
  const fetchDashboard = useCallback(async () => {
    try {
      setLoading(true);

      const res = await getAdminDashboard();
      const data = res?.data || {};

      // ‚ö†Ô∏è ÿØÿπŸÖ ÿ£ŸÉÿ´ÿ± ŸÖŸÜ ÿ¥ŸÉŸÑ Response (ÿπÿ¥ÿßŸÜ ÿßŸÑÿµŸÅÿ≠ÿ© ŸÖÿß ÿ™ŸÅÿ∂ÿßÿ¥)
      const statistics = data.statistics || {};
      const latestData = data.latest || data;

      setStats({
        users: statistics.users ?? latestData.users?.length ?? 0,
        categories: statistics.categories ?? latestData.categories?.length ?? 0,
        products: statistics.products ?? latestData.products?.length ?? 0,
        customers: statistics.customers ?? latestData.customers?.length ?? 0,
        orders: statistics.orders ?? latestData.orders?.length ?? 0,
        employees: statistics.employees ?? latestData.employees?.length ?? 0,
        sales: statistics.sales ?? latestData.sales?.length ?? 0,
        reservations:
          statistics.reservations ?? latestData.reservations?.length ?? 0,
      });

      setLatest({
        users: Array.isArray(latestData.users) ? latestData.users : [],
        categories: Array.isArray(latestData.categories)
          ? latestData.categories
          : [],
        products: Array.isArray(latestData.products) ? latestData.products : [],
        customers: Array.isArray(latestData.customers)
          ? latestData.customers
          : [],
        orders: Array.isArray(latestData.orders) ? latestData.orders : [],
        employees: Array.isArray(latestData.employees)
          ? latestData.employees
          : [],
        sales: Array.isArray(latestData.sales) ? latestData.sales : [],
        reservations: Array.isArray(latestData.reservations)
          ? latestData.reservations
          : [],
      });
    } catch (err) {
      console.error("Dashboard error:", err);
      setLatest(EMPTY_LATEST);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchDashboard();
  }, [fetchDashboard]);

  // ===================== CRUD =====================
  const handleAdd = (table) => {
    setCurrentTable(table);
    setFormData({});
    setModalOpen(true);
  };

  const handleEdit = (table, item) => {
    setCurrentTable(table);
    setFormData(item);
    setModalOpen(true);
  };

  const handleDelete = async (table, id) => {
    const ok = await confirm(() =>
      deleteEntity(table, id).then(fetchDashboard)
    );
    if (!ok) return;
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("SUBMIT:", currentTable, formData);
    setModalOpen(false);
  };

  // ===================== UI =====================
  if (loading) {
    return (
      <div className="dashboard-loading">
        <h2>Loading dashboard...</h2>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <h1>Admin Dashboard</h1>

      {/* ===== STATS ===== */}
      <div className="stats-grid">
        {Object.entries(stats).map(([key, value]) => (
          <div key={key} className="stat-card">
            <div className="stat-icon">{iconMap[key]}</div>
            <div className="stat-title">{key}</div>
            <div className="stat-value">{value}</div>
          </div>
        ))}
      </div>

      {/* ===== TABLES ===== */}
      <div className="tables-grid">
        {Object.entries(latest).map(([table, rows]) => (
          <div key={table} className="table-box">
            <div className="table-header">
              <h3>
                {iconMap[table]} {table}
              </h3>
              <button onClick={() => handleAdd(table)}>+ Add</button>
            </div>

            {rows.length === 0 ? (
              <p className="empty">No data</p>
            ) : (
              <table>
                <thead>
                  <tr>
                    {Object.keys(rows[0])
                      .slice(0, 4)
                      .map((col) => (
                        <th key={col}>{col}</th>
                      ))}
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {rows.slice(0, 5).map((row, i) => (
                    <tr key={i}>
                      {Object.keys(row)
                        .slice(0, 4)
                        .map((col) => (
                          <td key={col}>
                            {String(row[col] ?? "-").slice(0, 30)}
                          </td>
                        ))}
                      <td>
                        <button onClick={() => handleEdit(table, row)}>
                          ‚úèÔ∏è
                        </button>
                        <button
                          onClick={() => handleDelete(table, row.id || row._id)}
                        >
                          üóëÔ∏è
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        ))}
      </div>

      <ConfirmModal />

      <CrudModal
        isOpen={modalOpen}
        title={`${formData.id ? "Edit" : "Add"} ${currentTable}`}
        formData={formData}
        onChange={handleChange}
        onSubmit={handleSubmit}
        onClose={() => setModalOpen(false)}
      />
    </div>
  );
};

export default Dashboard;
