import axios from "axios";

// ✅ إنشاء instance من axios مع الإعدادات الصحيحة
const api = axios.create({
  baseURL: "http://127.0.0.1:8000",
  withCredentials: true, // ⚠️ مهم جداً لإرسال الـ cookies
  headers: {
    Accept: "application/json",
    "Content-Type": "application/json",
  },
});

// ✅ Interceptor لإضافة الـ CSRF token تلقائياً
api.interceptors.request.use(
  (config) => {
    // الحصول على الـ CSRF token من الـ cookies
    const csrfToken = getCookie("XSRF-TOKEN");

    if (csrfToken) {
      config.headers["X-XSRF-TOKEN"] = csrfToken;
    }

    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// ✅ دالة مساعدة لقراءة الـ cookies
function getCookie(name) {
  const value = `; ${document.cookie}`;
  const parts = value.split(`; ${name}=`);
  if (parts.length === 2) return parts.pop().split(";").shift();
}

export default api;
