// src/components/admin/DashboardTable.jsx
const DashboardTable = ({ title, rows, onAdd, onEdit, onDelete }) => {
  if (!Array.isArray(rows)) return null;

  return (
    <div className="d-flex">
      <div className="table-box">
        <div className="table-header">
          <h3>{title}</h3>
          <button onClick={onAdd}>+ Add</button>
        </div>

        {rows.length === 0 ? (
          <p>No data</p>
        ) : (
          <table>
            <thead>
              <tr>
                {Object.keys(rows[0])
                  .slice(0, 4)
                  .map((k) => (
                    <th key={k}>{k}</th>
                  ))}
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {rows.slice(0, 5).map((row) => (
                <tr key={row.id}>
                  {Object.keys(row)
                    .slice(0, 4)
                    .map((k) => (
                      <td key={k}>{String(row[k])}</td>
                    ))}
                  <td>
                    <button onClick={() => onEdit(row)}>‚úèÔ∏è</button>
                    <button onClick={() => onDelete(row.id)}>üóëÔ∏è</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default DashboardTable;
