// src/pages/admin/Dashboard.jsx
import { useEffect, useState, useCallback } from "react";
import { getAdminDashboard, deleteEntity } from "../../services/adminApi";
import { useConfirm } from "../../hooks/useConfirm";
import CrudModal from "../../components/admin/CrudModal";

const EMPTY_LATEST = {
  users: [],
  categories: [],
  products: [],
  customers: [],
  orders: [],
  employees: [],
  sales: [],
  reservations: [],
};

const Dashboard = () => {
  // =====================
  // State
  // =====================
  const [stats, setStats] = useState({
    users: 0,
    categories: 0,
    products: 0,
    customers: 0,
    orders: 0,
    employees: 0,
    sales: 0,
    reservations: 0,
    revenue: 0,
  });

  const [latest, setLatest] = useState(EMPTY_LATEST);
  const [loading, setLoading] = useState(true);

  // CRUD Modal
  const [modalOpen, setModalOpen] = useState(false);
  const [currentTable, setCurrentTable] = useState("");
  const [formData, setFormData] = useState({});

  const { confirm, ConfirmModal } = useConfirm();

  // =====================
  // Icons
  // =====================
  const iconMap = {
    users: "ðŸ‘¥",
    categories: "ðŸ“",
    products: "ðŸ“¦",
    customers: "ðŸ‘¤",
    orders: "ðŸ›’",
    employees: "ðŸ’¼",
    sales: "ðŸ“ˆ",
    reservations: "ðŸ“…",
    revenue: "ðŸ’°",
  };

  // =====================
  // Fetch Dashboard
  // =====================
  const fetchDashboard = useCallback(() => {
    setLoading(true);

    getAdminDashboard()
      .then((res) => {
        const data = res?.data || {};

        const totalRevenue =
          (data.statistics?.orders || 0) * 100 +
          (data.statistics?.sales || 0) * 150;

        setStats({
          users: data.statistics?.users ?? 0,
          categories: data.statistics?.categories ?? 0,
          products: data.statistics?.products ?? 0,
          customers: data.statistics?.customers ?? 0,
          orders: data.statistics?.orders ?? 0,
          employees: data.statistics?.employees ?? 0,
          sales: data.statistics?.sales ?? 0,
          reservations: data.statistics?.reservations ?? 0,
          revenue: totalRevenue,
        });

        setLatest({
          users: data.latest?.users || [],
          categories: data.latest?.categories || [],
          products: data.latest?.products || [],
          customers: data.latest?.customers || [],
          orders: data.latest?.orders || [],
          employees: data.latest?.employees || [],
          sales: data.latest?.sales || [],
          reservations: data.latest?.reservations || [],
        });
      })
      .catch(() => setLatest(EMPTY_LATEST))
      .finally(() => setLoading(false));
  }, []);

  useEffect(() => {
    fetchDashboard();
    document.body.classList.add("dashboard-page");
    return () => document.body.classList.remove("dashboard-page");
  }, [fetchDashboard]);

  // =====================
  // CRUD Actions
  // =====================
  const handleAdd = (table) => {
    setCurrentTable(table);
    setFormData({});
    setModalOpen(true);
  };

  const handleEdit = (table, item) => {
    setCurrentTable(table);
    setFormData(item);
    setModalOpen(true);
  };

  const handleDelete = async (table, id) => {
    const ok = await confirm(
      "Delete Confirmation",
      `Are you sure you want to delete this item?`
    );
    if (!ok) return;

    deleteEntity(table, id).then(fetchDashboard);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Submit:", currentTable, formData);
    setModalOpen(false);
  };

  // =====================
  // UI
  // =====================
  if (loading) return <div className="dashboard-loading">Loading...</div>;

  return (
    <div className="dashboard-container">
      {/* ===== Statistics ===== */}
      <div className="stats-grid">
        {Object.entries(stats).map(([key, value]) => (
          <div key={key} className="stat-card">
            <div className="stat-card-icon">{iconMap[key]}</div>
            <div className="stat-title">{key}</div>
            <div className="stat-value">
              {key === "revenue" ? `$${value}` : value}
            </div>
          </div>
        ))}
      </div>

      {/* ===== Tables ===== */}
      <div className="tables-grid">
        {Object.entries(latest).map(([table, data]) => (
          <div key={table} className="table-container">
            <div className="table-header">
              <h3>
                {iconMap[table]} {table}
              </h3>
              <button onClick={() => handleAdd(table)}>+ Add</button>
            </div>
          </div>
        ))}
      </div>

      <ConfirmModal />

      <CrudModal
        isOpen={modalOpen}
        title={`${formData.id ? "Edit" : "Add"} ${currentTable}`}
        fields={Object.keys(formData)}
        formData={formData}
        onChange={handleChange}
        onSubmit={handleSubmit}
        onClose={() => setModalOpen(false)}
      />
    </div>
  );
};

export default Dashboard;
