// src/components/admin/CrudModal.jsx
import { useState, useEffect } from "react";
import "./CrudModal.css";

const CrudModal = ({
  isOpen,
  title,
  fields = [],
  formData = {},
  onSubmit,
  onClose,
}) => {
  const [localFormData, setLocalFormData] = useState({});
  const [errors, setErrors] = useState({});

  useEffect(() => {
    setLocalFormData(formData);
    setErrors({});
  }, [formData, isOpen]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setLocalFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));

    // مسح الخطأ عند التعديل
    if (errors[name]) {
      setErrors((prev) => ({ ...prev, [name]: "" }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // تحقق بسيط
    const newErrors = {};
    Object.keys(localFormData).forEach((key) => {
      if (!localFormData[key] && key !== "id") {
        newErrors[key] = "هذا الحقل مطلوب";
      }
    });

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    onSubmit(localFormData);
  };

  const getFieldType = (key) => {
    if (key.includes("email")) return "email";
    if (key.includes("password")) return "password";
    if (key.includes("date")) return "date";
    if (key.includes("time")) return "time";
    if (
      key.includes("price") ||
      key.includes("salary") ||
      key.includes("quantity")
    )
      return "number";
    if (key.includes("description") || key.includes("message"))
      return "textarea";
    return "text";
  };

  const getFieldLabel = (key) => {
    const labels = {
      name: "الاسم",
      email: "البريد الإلكتروني",
      title: "العنوان",
      description: "الوصف",
      price: "السعر",
      quantity: "الكمية",
      status: "الحالة",
      role: "الدور",
      salary: "الراتب",
      date: "التاريخ",
      time: "الوقت",
      persons: "عدد الأشخاص",
      message: "الرسالة",
    };

    return labels[key] || key.replace(/_/g, " ");
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3 className="modal-title">{title}</h3>
          <button className="modal-close" onClick={onClose}>
            ×
          </button>
        </div>

        <form onSubmit={handleSubmit} className="modal-form">
          <div className="modal-body">
            {fields.map((field, index) => {
              const type = getFieldType(field);
              const label = getFieldLabel(field);
              const value = localFormData[field] || "";

              return (
                <div key={index} className="form-group">
                  <label htmlFor={field} className="form-label">
                    {label}
                    {field !== "id" && <span className="required">*</span>}
                  </label>

                  {type === "textarea" ? (
                    <textarea
                      id={field}
                      name={field}
                      value={value}
                      onChange={handleChange}
                      className={`form-control ${errors[field] ? "error" : ""}`}
                      rows="3"
                      disabled={field === "id"}
                    />
                  ) : (
                    <input
                      type={type}
                      id={field}
                      name={field}
                      value={value}
                      onChange={handleChange}
                      className={`form-control ${errors[field] ? "error" : ""}`}
                      disabled={field === "id"}
                      min={type === "number" ? "0" : undefined}
                      step={type === "number" ? "0.01" : undefined}
                    />
                  )}

                  {errors[field] && (
                    <div className="error-message">{errors[field]}</div>
                  )}
                </div>
              );
            })}
          </div>

          <div className="modal-footer">
            <button type="button" className="btn-cancel" onClick={onClose}>
              إلغاء
            </button>
            <button type="submit" className="btn-submit">
              {formData.id ? "تحديث" : "إنشاء"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CrudModal;
