// src/services/adminApi.js
import axios from "axios";

const API = axios.create({
  baseURL: "http://127.0.0.1:8000/api",
});

API.interceptors.request.use((config) => {
  const token = localStorage.getItem("token");
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

export const getAdminDashboard = () => API.get("/admin/dashboard");
export const deleteEntity = (table, id) => axios.delete(`/${table}/${id}`);
// ===== Users =====
export const usersApi = {
  get: () => axios.get("/users"),
  create: (data) => axios.post("/users", data),
  update: (id, data) => axios.put(`/users/${id}`, data),
  delete: (id) => axios.delete(`/users/${id}`),
};

// ===== Categories =====
export const categoriesApi = {
  get: () => axios.get("/categories"),
  create: (data) => axios.post("/categories", data),
  update: (id, data) => axios.put(`/categories/${id}`, data),
  delete: (id) => axios.delete(`/categories/${id}`),
};

// ===== Products =====
export const productsApi = {
  get: () => axios.get("/products"),
  create: (data) => axios.post("/products", data),
  update: (id, data) => axios.put(`/products/${id}`, data),
  delete: (id) => axios.delete(`/products/${id}`),
};

// ===== Customers =====
export const customersApi = {
  get: () => axios.get("/customers"),
  create: (data) => axios.post("/customers", data),
  update: (id, data) => axios.put(`/customers/${id}`, data),
  delete: (id) => axios.delete(`/customers/${id}`),
};

// ===== Orders =====
export const ordersApi = {
  get: () => axios.get("/orders"),
  create: (data) => axios.post("/orders", data),
  update: (id, data) => axios.put(`/orders/${id}`, data),
  delete: (id) => axios.delete(`/orders/${id}`),
};

// ===== Employees =====
export const employeesApi = {
  get: () => axios.get("/employees"),
  create: (data) => axios.post("/employees", data),
  update: (id, data) => axios.put(`/employees/${id}`, data),
  delete: (id) => axios.delete(`/employees/${id}`),
};

// ===== Sales =====
export const salesApi = {
  get: () => axios.get("/sales"),
  create: (data) => axios.post("/sales", data),
  update: (id, data) => axios.put(`/sales/${id}`, data),
  delete: (id) => axios.delete(`/sales/${id}`),
};

// ===== Reservations =====
export const reservationsApi = {
  get: () => axios.get("/reservations"),
  create: (data) => axios.post("/reservations", data),
  update: (id, data) => axios.put(`/reservations/${id}`, data),
  delete: (id) => axios.delete(`/reservations/${id}`),
};
