import { useEffect, useState } from "react";
import { getAdminDashboard, deleteEntity } from "../../services/adminApi";
import StatCard from "../../components/admin/StatCard";
import LatestTable from "../../components/admin/LatestTable";
import { useConfirm } from "../../hooks/useConfirm";

const Dashboard = () => {
  const [stats, setStats] = useState(null);
  const [latest, setLatest] = useState({
    users: [],
    categories: [],
    products: [],
    customers: [],
    orders: [],
    employees: [],
    sales: [],
    reservations: [],
  });
  const [loading, setLoading] = useState(true);

  const { confirm, ConfirmModal } = useConfirm();

  const fetchDashboard = () => {
    setLoading(true);
    getAdminDashboard()
      .then((res) => {
        setStats(res.data.statistics);
        setLatest(res.data.latest || {});
      })
      .finally(() => setLoading(false));
  };

  useEffect(() => {
    fetchDashboard();
  }, []);

  const handleDelete = (table, id) => {
    confirm(() => {
      deleteEntity(table, id).then(fetchDashboard).catch(console.error);
    });
  };

  const handleEdit = (table, item) => {
    alert(`Edit ${table} ID: ${item.id}`);
  };

  const handleAdd = (table) => {
    alert(`Add new ${table}`);
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="p-6 space-y-6">
      {/* ===== Statistics ===== */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        {Object.entries(stats).map(([key, value]) => (
          <StatCard key={key} title={key} value={value} />
        ))}
      </div>

      {/* ===== Latest Tables ===== */}
      <div className="grid md:grid-cols-2 gap-6">
        {Object.entries(latest).map(([table, data]) => (
          <LatestTable
            key={table}
            title={`Latest ${table}`}
            data={Array.isArray(data) ? data : []}
            onAdd={() => handleAdd(table)}
            onEdit={(item) => handleEdit(table, item)}
            onDelete={(id) => handleDelete(table, id)}
          />
        ))}
      </div>

      <ConfirmModal />
    </div>
  );
};

export default Dashboard;
