import { useEffect, useState } from "react";
import { getAdminDashboard, deleteEntity } from "../../services/adminApi";
import StatCard from "../../components/admin/StatCard";
import LatestTable from "../../components/admin/LatestTable";
import { useConfirm } from "../../hooks/useConfirm";

const Dashboard = () => {
  const [stats, setStats] = useState(null);
  const [latest, setLatest] = useState({
    users: [],
    categories: [],
    products: [],
    customers: [],
    orders: [],
    employees: [],
    sales: [],
    reservations: [],
  });
  const [loading, setLoading] = useState(true);
  const [confirm, confirmModal] = useConfirm();
  // --- جلب البيانات ---
  const fetchDashboard = () => {
    setLoading(true);
    getAdminDashboard()
      .then((res) => {
        setStats(res.data.statistics);
        setLatest(res.data.latest);
      })
      .finally(() => setLoading(false));
  };

  useEffect(() => {
    fetchDashboard();
  }, []);

  // --- حذف عنصر ---
  const handleDelete = (table, id) => {
    if (!confirm("Are you sure you want to delete this item?")) return;
    deleteEntity(table, id)
      .then(() => {
        fetchDashboard(); // تحديث البيانات بعد الحذف
      })
      .catch((err) => console.error(err));
  };

  // --- تحرير عنصر (مثال) ---
  const handleEdit = (table, item) => {
    alert(`Open edit form for ${table} ID: ${item.id}`);
    // هنا ممكن تفتح modal أو redirect لصفحة edit
  };

  // --- إضافة عنصر (مثال) ---
  const handleAdd = (table) => {
    alert(`Open add form for ${table}`);
    // هنا ممكن تفتح modal أو redirect لصفحة add
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="p-6 space-y-6">
      {/* ===== Statistics ===== */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        <StatCard title="Users" value={stats.users} />
        <StatCard title="Categories" value={stats.categories} />
        <StatCard title="Products" value={stats.products} />
        <StatCard title="Customers" value={stats.customers} />
        <StatCard title="Orders" value={stats.orders} />
        <StatCard title="Employees" value={stats.employees} />
        <StatCard title="Sales" value={stats.sales} />
        <StatCard title="Reservations" value={stats.reservations} />
      </div>

      {/* ===== Latest Tables with CRUD ===== */}
      <div className="grid md:grid-cols-2 gap-6">
        {Object.entries(latest).map(([table, data]) => (
          <LatestTable
            key={table}
            title={`Latest ${table.charAt(0).toUpperCase() + table.slice(1)}`}
            data={data}
            columns={Object.keys(data[0] || {}).map((key) => ({
              key,
              label: key.replace("_", " ").toUpperCase(),
            }))}
            onAdd={() => handleAdd(table)}
            onEdit={(item) => handleEdit(table, item)}
            onDelete={(id) => handleDelete(table, id)}
          />
        ))}
      </div>
      <confirmModal />
    </div>
  );
};

export default Dashboard;
