import { NavLink } from "react-router-dom";

const AdminNavbar = () => {
  const links = [
    { name: "Dashboard", path: "/admin" },
    { name: "Customers", path: "/admin/customers" },
    { name: "Products", path: "/admin/products" },
    { name: "Orders", path: "/admin/orders" },
  ];

  return (
    <nav style={styles.nav}>
      <div style={styles.logo}>Admin Panel</div>
      <div style={styles.links}>
        {links.map((link) => (
          <NavLink
            key={link.path}
            to={link.path}
            style={({ isActive }) => ({
              ...styles.link,
              ...(isActive ? styles.active : {}),
            })}
          >
            {link.name}
          </NavLink>
        ))}
      </div>
      {!user ? (
        <>
          <li>
            <NavLink to="/login">Login</NavLink>
          </li>
          <li>
            <NavLink to="/register">Register</NavLink>
          </li>
        </>
      ) : (
        <>
          <li>
            <button
              onClick={handleLogout}
              className="btn-logout"
              style={{
                background: "none",
                border: "none",
                cursor: "pointer",
              }}
            >
              Logout
            </button>
          </li>
        </>
      )}{" "}
    </nav>
  );
};

const styles = {
  nav: {
    height: "60px",
    background: "#111827",
    color: "#fff",
    display: "flex",
    alignItems: "center",
    padding: "0 20px",
    justifyContent: "space-between",
  },
  logo: {
    fontWeight: "bold",
    fontSize: "18px",
  },
  links: {
    display: "flex",
    gap: "20px",
  },
  link: {
    color: "#9ca3af",
    textDecoration: "none",
    fontSize: "14px",
  },
  active: {
    color: "#fff",
    borderBottom: "2px solid #3b82f6",
    paddingBottom: "4px",
  },
  logout: {
    background: "#ef4444",
    border: "none",
    color: "#fff",
    padding: "6px 12px",
    cursor: "pointer",
    borderRadius: "4px",
  },
};

export default AdminNavbar;
