// src/pages/admin/Dashboard.jsx
import { useEffect, useState } from "react";
import { getAdminDashboard } from "../../services/adminApi";
import StatCard from "../../components/admin/StatCard";
import LatestTable from "../../components/admin/LatestTable";

const Dashboard = () => {
  const [stats, setStats] = useState(null);
  const [latest, setLatest] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    getAdminDashboard()
      .then((res) => {
        setStats(res.data.statistics);
        setLatest(res.data.latest);
      })
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <p>Loading...</p>;

  return (
    <div className="p-6 space-y-6">
      {/* ===== Statistics ===== */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        <StatCard title="Users" value={stats.users} />
        <StatCard title="Categories" value={stats.categories} />
        <StatCard title="Products" value={stats.products} />
        <StatCard title="Customers" value={stats.customers} />
        <StatCard title="Orders" value={stats.orders} />
        <StatCard title="Employees" value={stats.employees} />
        <StatCard title="Sales" value={stats.sales} />
        <StatCard title="Reservations" value={stats.reservations} />
      </div>

      {/* ===== Latest Tables ===== */}
      <div className="grid md:grid-cols-2 gap-6">
        <LatestTable
          title="Latest Users"
          data={latest.users}
          columns={[
            { key: "id", label: "ID" },
            { key: "name", label: "Name" },
            { key: "email", label: "Email" },
            { key: "role", label: "Role" },
            { key: "status", label: "Status" },
            { key: "created_at", label: "Date" },
          ]}
        />

        <LatestTable
          title="Latest Orders"
          data={latest.orders}
          columns={[
            { key: "id", label: "ID" },
            { key: "title", label: "Title" },
            { key: "description", label: "Description" },
            { key: "title", label: "Title" },
            { key: "title", label: "Title" },
            { key: "title", label: "Title" },
            { key: "created_at", label: "Date" },
          ]}
        />
      </div>
    </div>
  );
};

export default Dashboard;
