// src/components/admin/StatCard.jsx
import PropTypes from "prop-types";

const StatCard = ({ title, value, change, type = "primary", icon = "üìä" }) => {
  const typeClasses = {
    users: "users",
    categories: "categories",
    products: "products",
    customers: "customers",
    orders: "orders",
    employees: "employees",
    sales: "sales",
    reservations: "reservations",
    revenue: "revenue",
    primary: "primary",
    success: "success",
    warning: "warning",
    danger: "danger",
    info: "info",
    dark: "dark",
  };

  const iconMap = {
    users: "üë•",
    categories: "üìÅ",
    products: "üì¶",
    customers: "üë§",
    orders: "üõí",
    employees: "üíº",
    sales: "üìà",
    reservations: "üìÖ",
    revenue: "üí∞",
    default: "üìä",
  };

  return (
    <div className={`stat-card ${typeClasses[type] || "primary"}`}>
      <div className="stat-card-icon">
        {iconMap[type] || icon || iconMap.default}
      </div>
      <div className="stat-title">{title}</div>
      <div className="stat-value">
        {typeof value === "number" && value > 9999
          ? `${(value / 1000).toFixed(1)}K`
          : value.toLocaleString()}
      </div>
      {change && (
        <div
          className={`stat-change ${
            change.startsWith("+") ? "positive" : "negative"
          }`}
        >
          {change}
        </div>
      )}
    </div>
  );
};

StatCard.propTypes = {
  title: PropTypes.string.isRequired,
  value: PropTypes.oneOfType([PropTypes.number, PropTypes.string]).isRequired,
  change: PropTypes.string,
  type: PropTypes.string,
  icon: PropTypes.string,
};

export default StatCard;
