import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { deleteEntity } from "../../services/adminApi";
import api from "../../services/axios";
import { FaEdit, FaTrash, FaPlus, FaSpinner, FaEye } from "react-icons/fa";

const CrudTable = () => {
  const { table } = useParams();
  const navigate = useNavigate();

  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedRow, setSelectedRow] = useState(null);

  const fetchTableData = async () => {
    setLoading(true);
    try {
      const res = await api.get(`/admin/${table}`);
      setData(Array.isArray(res.data) ? res.data : []);
    } catch (err) {
      console.error(err);
      setData([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTableData();
  }, [table]);

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this item?")) return;
    await deleteEntity(table, id);
    fetchTableData();
  };

  const handleViewDetails = (item) => {
    setSelectedRow(selectedRow?.id === item.id ? null : item);
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-center">
          <FaSpinner className="animate-spin text-4xl text-blue-600 mx-auto mb-4" />
          <p className="text-gray-600">Loading table data...</p>
        </div>
      </div>
    );
  }

  const columns = data.length ? Object.keys(data[0]) : [];

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      {/* Header Section */}
      <div className="mb-8">
        <div className="flex justify-between items-center mb-4">
          <h1 className="text-3xl font-bold text-gray-800 capitalize">
            {table.replace(/_/g, " ")} Management
          </h1>
          <button
            onClick={() => navigate(`/admin/${table}/create`)}
            className="flex items-center gap-2 bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 px-5 rounded-lg shadow-md transition-all duration-200 hover:shadow-lg"
          >
            <FaPlus />
            Add New
          </button>
        </div>

        <div className="bg-white p-4 rounded-lg shadow-sm border border-gray-200">
          <div className="flex items-center gap-4">
            <div className="px-3 py-1 bg-blue-100 text-blue-800 rounded-full font-medium">
              {data.length} {data.length === 1 ? "Record" : "Records"}
            </div>
            <div className="text-gray-600">
              Manage all records in the{" "}
              <span className="font-semibold capitalize">{table}</span> table
            </div>
          </div>
        </div>
      </div>

      {/* Table Section */}
      {data.length === 0 ? (
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-12 text-center">
          <div className="max-w-md mx-auto">
            <div className="text-gray-400 mb-4">
              <svg
                className="w-16 h-16 mx-auto"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="1.5"
                  d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10"
                ></path>
              </svg>
            </div>
            <h3 className="text-xl font-semibold text-gray-700 mb-2">
              No Data Found
            </h3>
            <p className="text-gray-500 mb-6">
              There are no records in this table yet. Start by adding a new one.
            </p>
            <button
              onClick={() => navigate(`/admin/${table}/create`)}
              className="inline-flex items-center gap-2 bg-blue-600 hover:bg-blue-700 text-white font-medium py-2.5 px-5 rounded-lg transition-colors"
            >
              <FaPlus />
              Create First Record
            </button>
          </div>
        </div>
      ) : (
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden">
          {/* Responsive Container */}
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  {columns.map((col) => (
                    <th
                      key={col}
                      className="px-6 py-4 text-left text-xs font-semibold text-gray-700 uppercase tracking-wider"
                    >
                      {col.replace(/_/g, " ")}
                    </th>
                  ))}
                  <th className="px-6 py-4 text-left text-xs font-semibold text-gray-700 uppercase tracking-wider">
                    Actions
                  </th>
                </tr>
              </thead>

              <tbody className="bg-white divide-y divide-gray-200">
                {data.map((item) => (
                  <>
                    <tr
                      key={item.id}
                      className="hover:bg-gray-50 transition-colors"
                    >
                      {columns.map((col) => (
                        <td
                          key={col}
                          className="px-6 py-4 whitespace-nowrap text-sm text-gray-800 max-w-xs truncate"
                          title={String(item[col] ?? "-")}
                        >
                          {String(item[col] ?? "-")}
                        </td>
                      ))}
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                        <div className="flex items-center gap-2">
                          <button
                            onClick={() => handleViewDetails(item)}
                            className="flex items-center gap-1 text-blue-600 hover:text-blue-800 bg-blue-50 hover:bg-blue-100 p-2 rounded-md transition-colors"
                            title="View Details"
                          >
                            <FaEye />
                          </button>
                          <button
                            onClick={() =>
                              navigate(`/admin/${table}/${item.id}/edit`)
                            }
                            className="flex items-center gap-1 text-green-600 hover:text-green-800 bg-green-50 hover:bg-green-100 p-2 rounded-md transition-colors"
                            title="Edit"
                          >
                            <FaEdit />
                          </button>
                          <button
                            onClick={() => handleDelete(item.id)}
                            className="flex items-center gap-1 text-red-600 hover:text-red-800 bg-red-50 hover:bg-red-100 p-2 rounded-md transition-colors"
                            title="Delete"
                          >
                            <FaTrash />
                          </button>
                        </div>
                      </td>
                    </tr>

                    {/* Expanded Row Details */}
                    {selectedRow?.id === item.id && (
                      <tr className="bg-blue-50">
                        <td colSpan={columns.length + 1} className="px-6 py-4">
                          <div className="bg-white p-4 rounded-lg border border-blue-200">
                            <h4 className="font-semibold text-gray-800 mb-3 flex items-center gap-2">
                              <FaEye className="text-blue-600" />
                              Record Details
                            </h4>
                            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                              {columns.map((col) => (
                                <div key={col} className="mb-2">
                                  <div className="text-xs font-semibold text-gray-500 uppercase tracking-wider">
                                    {col.replace(/_/g, " ")}
                                  </div>
                                  <div className="text-sm text-gray-800 break-words mt-1 p-2 bg-gray-50 rounded">
                                    {String(item[col] ?? "Empty")}
                                  </div>
                                </div>
                              ))}
                            </div>
                            <div className="flex justify-end mt-4 pt-4 border-t border-gray-200">
                              <button
                                onClick={() => setSelectedRow(null)}
                                className="text-sm text-gray-600 hover:text-gray-800 font-medium"
                              >
                                Close Details
                              </button>
                            </div>
                          </div>
                        </td>
                      </tr>
                    )}
                  </>
                ))}
              </tbody>
            </table>
          </div>

          {/* Footer */}
          <div className="bg-gray-50 px-6 py-3 border-t border-gray-200">
            <div className="flex justify-between items-center">
              <div className="text-sm text-gray-600">
                Showing <span className="font-semibold">{data.length}</span>{" "}
                records
              </div>
              <div className="text-sm text-gray-600">
                Table:{" "}
                <span className="font-mono bg-gray-100 px-2 py-1 rounded">
                  {table}
                </span>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Quick Stats */}
      {data.length > 0 && (
        <div className="mt-6 grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="bg-white p-4 rounded-lg shadow-sm border border-gray-200">
            <div className="text-sm text-gray-500">Total Records</div>
            <div className="text-2xl font-bold text-gray-800">
              {data.length}
            </div>
          </div>
          <div className="bg-white p-4 rounded-lg shadow-sm border border-gray-200">
            <div className="text-sm text-gray-500">Columns</div>
            <div className="text-2xl font-bold text-gray-800">
              {columns.length}
            </div>
          </div>
          <div className="bg-white p-4 rounded-lg shadow-sm border border-gray-200">
            <div className="text-sm text-gray-500">Actions Available</div>
            <div className="text-2xl font-bold text-gray-800">3</div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CrudTable;
