// src/services/adminApi.js
import axios from "axios";

// إنشاء instance واحد لـ axios
const API = axios.create({
  baseURL: "http://127.0.0.1:8000/api",
  timeout: 10000, // 10 ثواني
  headers: {
    "Content-Type": "application/json",
    Accept: "application/json",
  },
});

// Interceptor للإضافة التلقائية للـ Token
API.interceptors.request.use((config) => {
  const token = localStorage.getItem("token");
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Interceptor لمعالجة الأخطاء
API.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem("token");
      window.location.href = "/login";
    }
    return Promise.reject(error);
  }
);

// ===== وظائف مساعدة =====
const createEntityApi = (entity) => ({
  getAll: () => API.get(`/${entity}`),
  getOne: (id) => API.get(`/${entity}/${id}`),
  create: (data) => API.post(`/${entity}`, data),
  update: (id, data) => API.put(`/${entity}/${id}`, data),
  delete: (id) => API.delete(`/${entity}/${id}`),
  search: (params) => API.get(`/${entity}/search`, { params }),
});

// ===== Dashboard API =====
export const getAdminDashboard = () => API.get("/admin/dashboard");

// ===== General CRUD Operations =====
export const entityApi = {
  // دالة عامة للحذف
  delete: (entity, id) => API.delete(`/${entity}/${id}`),

  // دالة عامة لجميع العمليات
  crud: (entity) => createEntityApi(entity),

  // دالة لعمليات متعددة
  bulkDelete: (entity, ids) => API.post(`/${entity}/bulk-delete`, { ids }),

  // دالة للتصدير
  export: (entity, format = "csv") =>
    API.get(`/${entity}/export`, {
      params: { format },
      responseType: "blob",
    }),
};

// ===== Users API =====
export const usersApi = {
  ...createEntityApi("users"),
  // وظائف خاصة بالمستخدمين
  changeStatus: (id, status) => API.patch(`/users/${id}/status`, { status }),
  changeRole: (id, role) => API.patch(`/users/${id}/role`, { role }),
  getStats: () => API.get("/users/stats"),
  getLatest: (limit = 5) => API.get(`/users/latest?limit=${limit}`),
};

// ===== Categories API =====
export const categoriesApi = {
  ...createEntityApi("categories"),
  // وظائف خاصة بالفئات
  withProducts: () => API.get("/categories/with-products"),
  getTree: () => API.get("/categories/tree"),
  getLatest: (limit = 5) => API.get(`/categories/latest?limit=${limit}`),
};

// ===== Products API =====
export const productsApi = {
  ...createEntityApi("products"),
  // وظائف خاصة بالمنتجات
  byCategory: (categoryId) => API.get(`/products/category/${categoryId}`),
  withLowStock: () => API.get("/products/low-stock"),
  getLatest: (limit = 5) => API.get(`/products/latest?limit=${limit}`),
  updateStock: (id, quantity) =>
    API.patch(`/products/${id}/stock`, { quantity }),
};

// ===== Customers API =====
export const customersApi = {
  ...createEntityApi("customers"),
  // وظائف خاصة بالعملاء
  getOrders: (id) => API.get(`/customers/${id}/orders`),
  getLatest: (limit = 5) => API.get(`/customers/latest?limit=${limit}`),
  getStats: () => API.get("/customers/stats"),
};

// ===== Orders API =====
export const ordersApi = {
  ...createEntityApi("orders"),
  // وظائف خاصة بالطلبات
  updateStatus: (id, status) => API.patch(`/orders/${id}/status`, { status }),
  getByCustomer: (customerId) => API.get(`/orders/customer/${customerId}`),
  getLatest: (limit = 5) => API.get(`/orders/latest?limit=${limit}`),
  getStats: () => API.get("/orders/stats"),
  getRevenue: () => API.get("/orders/revenue"),
};

// ===== Employees API =====
export const employeesApi = {
  ...createEntityApi("employees"),
  // وظائف خاصة بالموظفين
  getSales: (id) => API.get(`/employees/${id}/sales`),
  getLatest: (limit = 5) => API.get(`/employees/latest?limit=${limit}`),
  getStats: () => API.get("/employees/stats"),
  updateSalary: (id, salary) =>
    API.patch(`/employees/${id}/salary`, { salary }),
};

// ===== Sales API =====
export const salesApi = {
  ...createEntityApi("sales"),
  // وظائف خاصة بالمبيعات
  getByEmployee: (employeeId) => API.get(`/sales/employee/${employeeId}`),
  getLatest: (limit = 5) => API.get(`/sales/latest?limit=${limit}`),
  getStats: () => API.get("/sales/stats"),
  getDaily: () => API.get("/sales/daily"),
  getMonthly: () => API.get("/sales/monthly"),
};

// ===== Reservations API =====
export const reservationsApi = {
  ...createEntityApi("reservations"),
  // وظائف خاصة بالحجوزات
  updateStatus: (id, status) =>
    API.patch(`/reservations/${id}/status`, { status }),
  confirm: (id) => API.post(`/reservations/${id}/confirm`),
  cancel: (id) => API.post(`/reservations/${id}/cancel`),
  getByDate: (date) => API.get(`/reservations/date/${date}`),
  getLatest: (limit = 5) => API.get(`/reservations/latest?limit=${limit}`),
  getStats: () => API.get("/reservations/stats"),
};

// ===== Statistics API =====
export const statisticsApi = {
  getOverview: () => API.get("/statistics/overview"),
  getGrowth: (period = "monthly") =>
    API.get(`/statistics/growth?period=${period}`),
  getRevenue: () => API.get("/statistics/revenue"),
  getTopProducts: (limit = 10) =>
    API.get(`/statistics/top-products?limit=${limit}`),
  getTopCustomers: (limit = 10) =>
    API.get(`/statistics/top-customers?limit=${limit}`),
};

// ===== Export All APIs =====
export default {
  // API العامة
  API,

  // Dashboard
  getAdminDashboard,

  // General CRUD
  entityApi,

  // Entity-specific APIs
  usersApi,
  categoriesApi,
  productsApi,
  customersApi,
  ordersApi,
  employeesApi,
  salesApi,
  reservationsApi,

  // Statistics
  statisticsApi,

  // وظائف مساعدة
  // يمكن استخدامها في أي مكان
  createEntityApi,
};
