// src/services/adminApi.js
import axios from "./axios";

export const getAdminDashboard = () => axios.get("/admin/dashboard");

// إنشاء عنصر
export const createEntity = (table, data) => {
  return axios.post(`/admin/${table}`, data);
};

// تحديث عنصر
export const updateEntity = (table, id, data) => {
  return axios.put(`/admin/${table}/${id}`, data);
};

// حذف عنصر
export const deleteEntity = (table, id) => {
  return axios.delete(`/admin/${table}/${id}`);
};

export const getEntityById = (table, id) => {
  return axios.get(`/api/${table}/${id}`);
};
