import React, { useState } from "react";
import Navbar from "../Home/Navbar";
import Footer from "../Home/Footer";

function AllContact() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    persons: "",
    date: "",
    time: "",
    message: "",
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://8000/api/reservations", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
          Authorization: `Bearer ${token}`, // مهم جداً للمسارات المحمية
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        alert("Reservation sent successfully ✅");
        setFormData({
          name: "",
          email: "",
          persons: "",
          date: "",
          time: "",
          message: "",
        });
      } else {
        alert(data.message || "Something went wrong ❌");
      }
    } catch (error) {
      console.error(error);
      alert("Server error ❌");
    }
  };

  return (
    <div>
      <Navbar />

      <section id="contact">
        <div className="container">
          <div className="heading">
            <span className="text-primary">What Happens Here</span>
            <h2>Reservation Area</h2>
          </div>

          <div className="row">
            <div className="map">
              <iframe
                src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d884780.515123437!2d31.502804734375005!3d29.974432300000007!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x145856f81d43dbed%3A0x42cb3cb3dfd7e8b6!2sCosta%20Coffee!5e0!3m2!1sar!2seg!4v1749718038688!5m2!1sar!2seg"
                width="600"
                height="450"
                style={{ border: 0 }}
                allowFullScreen
                loading="lazy"
                referrerPolicy="no-referrer-when-downgrade"
              ></iframe>
            </div>

            <div className="contact-content">
              <h3>Book a Table</h3>

              <form onSubmit={handleSubmit}>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  placeholder="Your Name"
                />

                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  id="email"
                  placeholder="Your Email"
                />

                <input
                  type="number"
                  name="persons"
                  value={formData.persons}
                  onChange={handleChange}
                  id="persons"
                  placeholder="Persons"
                />

                <input
                  type="date"
                  name="date"
                  value={formData.date}
                  onChange={handleChange}
                  id="date"
                />

                <input
                  type="time"
                  name="time"
                  value={formData.time}
                  onChange={handleChange}
                  id="time"
                />

                <textarea
                  name="message"
                  id="message"
                  value={formData.message}
                  onChange={handleChange}
                  placeholder="Your Message"
                  rows="6"
                ></textarea>

                <button type="submit" className="btn-submit">
                  Book a Table
                </button>
              </form>
            </div>
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
}

export default AllContact;
