import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { deleteEntity } from "../../services/adminApi";
import api from "../../services/axios";

const CrudTable = () => {
  const { table } = useParams();
  const navigate = useNavigate();

  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchTableData = async () => {
    setLoading(true);
    try {
      const res = await api.get(`/admin/${table}`);
      setData(Array.isArray(res.data) ? res.data : []);
    } catch (err) {
      console.error(err);
      setData([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTableData();
  }, [table]);

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure?")) return;
    await deleteEntity(table, id);
    fetchTableData();
  };

  if (loading) return <p>Loading...</p>;

  const columns = data.length ? Object.keys(data[0]) : [];

  return (
    <div className="p-6">
      <h2>{table.toUpperCase()}</h2>

      <button onClick={() => navigate(`/admin/${table}/create`)}>
        + Add New
      </button>

      {data.length === 0 ? (
        <p>No data found</p>
      ) : (
        <table className="btn btn-dark w-75">
          <thead>
            <tr>
              {columns.map((col) => (
                <th key={col}>{col}</th>
              ))}
              <th>Actions</th>
            </tr>
          </thead>

          <tbody>
            {data.map((item) => (
              <tr key={item.id}>
                {columns.map((col) => (
                  <td key={col}>{String(item[col] ?? "-")}</td>
                ))}
                <td>
                  <button
                    onClick={() => navigate(`/admin/${table}/${item.id}/edit`)}
                  >
                    Edit
                  </button>
                  <button onClick={() => handleDelete(item.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default CrudTable;
