// src/pages/admin/Dashboard.jsx
import { useEffect, useState, useCallback } from "react";
import { getAdminDashboard, deleteEntity } from "../../services/adminApi";
import StatCard from "../../components/admin/StatCard";
import LatestTable from "../../components/admin/LatestTable";
import { useConfirm } from "../../hooks/useConfirm";
import "./Dashboard.css"; // ÿßÿ≥ÿ™Ÿäÿ±ÿßÿØ ŸÖŸÑŸÅ CSS ÿßŸÑÿ¨ÿØŸäÿØ

const EMPTY_LATEST = {
  users: [],
  categories: [],
  products: [],
  customers: [],
  orders: [],
  employees: [],
  sales: [],
  reservations: [],
};

const Dashboard = () => {
  const [stats, setStats] = useState({
    users: 0,
    categories: 0,
    products: 0,
    customers: 0,
    orders: 0,
    employees: 0,
    sales: 0,
    reservations: 0,
  });

  const [latest, setLatest] = useState(EMPTY_LATEST);
  const [loading, setLoading] = useState(true);

  const { confirm, ConfirmModal } = useConfirm();

  // =====================
  // Fetch Dashboard Data
  // =====================
  const fetchDashboard = useCallback(() => {
    setLoading(true);

    getAdminDashboard()
      .then((res) => {
        const data = res?.data || {};

        // ---- Statistics (numbers only)
        setStats({
          users: data.statistics?.users ?? 0,
          categories: data.statistics?.categories ?? 0,
          products: data.statistics?.products ?? 0,
          customers: data.statistics?.customers ?? 0,
          orders: data.statistics?.orders ?? 0,
          employees: data.statistics?.employees ?? 0,
          sales: data.statistics?.sales ?? 0,
          reservations: data.statistics?.reservations ?? 0,
        });

        // ---- Latest data (arrays only)
        setLatest({
          users: Array.isArray(data.latest?.users) ? data.latest.users : [],
          categories: Array.isArray(data.latest?.categories)
            ? data.latest.categories
            : [],
          products: Array.isArray(data.latest?.products)
            ? data.latest.products
            : [],
          customers: Array.isArray(data.latest?.customers)
            ? data.latest.customers
            : [],
          orders: Array.isArray(data.latest?.orders) ? data.latest.orders : [],
          employees: Array.isArray(data.latest?.employees)
            ? data.latest.employees
            : [],
          sales: Array.isArray(data.latest?.sales) ? data.latest.sales : [],
          reservations: Array.isArray(data.latest?.reservations)
            ? data.latest.reservations
            : [],
        });
      })
      .catch((err) => {
        console.error("Dashboard error:", err);
        setLatest(EMPTY_LATEST);
      })
      .finally(() => setLoading(false));
  }, []);

  useEffect(() => {
    fetchDashboard();
  }, [fetchDashboard]);

  // =====================
  // CRUD Actions
  // =====================
  const handleDelete = async (table, id) => {
    const ok = await confirm(
      "Delete Confirmation",
      "Are you sure you want to delete this item?"
    );

    if (!ok) return;

    deleteEntity(table, id).then(fetchDashboard).catch(console.error);
  };

  const handleEdit = (table, item) => {
    console.log("Edit:", table, item);
    // redirect ÿ£Ÿà modal
  };

  const handleAdd = (table) => {
    console.log("Add:", table);
    // redirect ÿ£Ÿà modal
  };

  if (loading)
    return <div className="dashboard-loading">Loading Dashboard...</div>;

  return (
    <div className="dashboard-container">
      {/* ================= Header ================= */}
      <div className="dashboard-header">
        <h1>Admin Dashboard</h1>
        <p>Overview of your application statistics and latest activities</p>
      </div>

      {/* ================= Statistics ================= */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-title">Total Users</div>
          <div className="stat-value">{stats.users.toLocaleString()}</div>
          <div className="stat-change">+12% from last month</div>
        </div>
        <div className="stat-card">
          <div className="stat-title">Categories</div>
          <div className="stat-value">{stats.categories.toLocaleString()}</div>
          <div className="stat-change">+5% from last month</div>
        </div>
        <div className="stat-card">
          <div className="stat-title">Products</div>
          <div className="stat-value">{stats.products.toLocaleString()}</div>
          <div className="stat-change">+18% from last month</div>
        </div>
        <div className="stat-card">
          <div className="stat-title">Customers</div>
          <div className="stat-value">{stats.customers.toLocaleString()}</div>
          <div className="stat-change">+8% from last month</div>
        </div>
        <div className="stat-card">
          <div className="stat-title">Orders</div>
          <div className="stat-value">{stats.orders.toLocaleString()}</div>
          <div className="stat-change">+23% from last month</div>
        </div>
        <div className="stat-card">
          <div className="stat-title">Employees</div>
          <div className="stat-value">{stats.employees.toLocaleString()}</div>
          <div className="stat-change">+2% from last month</div>
        </div>
        <div className="stat-card">
          <div className="stat-title">Sales</div>
          <div className="stat-value">${stats.sales.toLocaleString()}</div>
          <div className="stat-change">+15% from last month</div>
        </div>
        <div className="stat-card">
          <div className="stat-title">Reservations</div>
          <div className="stat-value">
            {stats.reservations.toLocaleString()}
          </div>
          <div className="stat-change">+7% from last month</div>
        </div>
      </div>

      {/* ================= Latest Tables ================= */}
      <div className="tables-grid">
        {Object.entries(latest).map(([table, data]) => {
          if (!Array.isArray(data)) return null;

          const columns =
            data.length > 0
              ? Object.keys(data[0]).map((key) => ({
                  key,
                  label: key.replace(/_/g, " ").toUpperCase(),
                }))
              : [];

          return (
            <div key={table} className="table-container">
              <div className="table-header">
                <h3>Latest {table.charAt(0).toUpperCase() + table.slice(1)}</h3>
                <div className="table-actions">
                  <button
                    className="action-btn"
                    onClick={() => handleAdd(table)}
                  >
                    + Add New
                  </button>
                </div>
              </div>
              <div className="table-content">
                {data.length > 0 ? (
                  <table className="custom-table">
                    <thead>
                      <tr>
                        {columns.slice(0, 3).map((col) => (
                          <th key={col.key}>{col.label}</th>
                        ))}
                        <th>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {data.slice(0, 5).map((item, index) => (
                        <tr key={index}>
                          {columns.slice(0, 3).map((col) => (
                            <td key={col.key}>
                              {typeof item[col.key] === "object"
                                ? JSON.stringify(item[col.key]).substring(
                                    0,
                                    30
                                  ) + "..."
                                : String(item[col.key]).substring(0, 50)}
                            </td>
                          ))}
                          <td className="table-actions-cell">
                            <button
                              className="icon-btn edit"
                              onClick={() => handleEdit(table, item)}
                              title="Edit"
                            >
                              ‚úèÔ∏è
                            </button>
                            <button
                              className="icon-btn delete"
                              onClick={() =>
                                handleDelete(
                                  table,
                                  item.id || item._id || index
                                )
                              }
                              title="Delete"
                            >
                              üóëÔ∏è
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                ) : (
                  <div className="no-data">
                    <div className="no-data-icon">üìä</div>
                    <p>No {table} data available</p>
                  </div>
                )}
              </div>
            </div>
          );
        })}
      </div>

      {/* ================= Confirm Modal ================= */}
      <ConfirmModal />
    </div>
  );
};

export default Dashboard;
