import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../../../services/axios";
import { deleteEntity } from "../../../services/adminApi";

const CrudTable = () => {
  const { table } = useParams();
  const navigate = useNavigate();

  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState("");
  const [page, setPage] = useState(1);
  const [lastPage, setLastPage] = useState(1);

  const fetchTableData = async () => {
    setLoading(true);
    try {
      const res = await api.get(`/admin/${table}`, {
        params: { search, page },
      });

      setData(res.data.data || []);
      setLastPage(res.data.last_page || 1);
    } catch (err) {
      console.error(err);
      setData([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTableData();
  }, [table, page, search]);

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure?")) return;
    await deleteEntity(table, id);
    fetchTableData();
  };

  if (loading) return <p>Loading...</p>;

  const columns = data.length ? Object.keys(data[0]) : [];

  return (
    <div className="p-6">
      <h2>{table.toUpperCase()}</h2>

      {/* Search */}
      <input
        type="text"
        placeholder="Search..."
        value={search}
        onChange={(e) => {
          setSearch(e.target.value);
          setPage(1);
        }}
      />

      <button onClick={() => navigate(`/admin/${table}/create`)}>
        + Add New
      </button>

      {data.length === 0 ? (
        <p>No data found</p>
      ) : (
        <table>
          <thead>
            <tr>
              {columns.map((col) => (
                <th key={col}>{col}</th>
              ))}
              <th>Actions</th>
            </tr>
          </thead>

          <tbody>
            {data.map((item) => (
              <tr key={item.id}>
                {columns.map((col) => (
                  <td key={col}>{String(item[col] ?? "-")}</td>
                ))}
                <td>
                  <button
                    onClick={() => navigate(`/admin/${table}/${item.id}/edit`)}
                  >
                    Edit
                  </button>
                  <button onClick={() => handleDelete(item.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* Pagination */}
      <div style={{ marginTop: "15px" }}>
        <button disabled={page === 1} onClick={() => setPage(page - 1)}>
          Prev
        </button>
        <span style={{ margin: "0 10px" }}>
          Page {page} of {lastPage}
        </span>
        <button disabled={page === lastPage} onClick={() => setPage(page + 1)}>
          Next
        </button>
      </div>
    </div>
  );
};

export default CrudTable;
