import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import {
  createEntity,
  updateEntity,
  getEntityById,
} from "../../../services/adminApi";

// === schema لكل جدول (حدد الحقول المطلوبة لكل جدول) ===
const tableSchemas = {
  users: { name: "", email: "", password: "", role: "", status: "" },
  products: {
    title: "",
    description: "",
    price: 0,
    quantity: 0,
    category_id: "",
  },
  categories: { title_en: "", description_en: "", cate_image: "" },
  customers: { name: "", email: "", status: "", password: "" },
  orders: {
    title_en: "",
    description_en: "",
    product_id: "",
    customer_id: "",
    quantity: 1,
  },
  employees: { name: "", email: "", password: "", salary: 0 },
  sales: { title: "", description: "", price: 0, quantity: 0, employee_id: "" },
  reservations: {
    name: "",
    email: "",
    persons: 1,
    date: "",
    time: "",
    message: "",
  },
};

const CrudForm = () => {
  const navigate = useNavigate();
  const { table, id } = useParams();
  const isEdit = Boolean(id);

  const [formData, setFormData] = useState({});
  const [loading, setLoading] = useState(false);

  // === Load data (Edit mode) ===
  useEffect(() => {
    if (!tableSchemas[table]) {
      console.error("Unknown table:", table);
      return;
    }

    if (isEdit) {
      setLoading(true);
      getEntityById(table, id)
        .then((res) => setFormData(res.data))
        .catch((err) => console.error(err))
        .finally(() => setLoading(false));
    } else {
      // Create mode → use initial schema
      setFormData(tableSchemas[table]);
    }
  }, [table, id, isEdit]);

  // === Handlers ===
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    const action = isEdit
      ? updateEntity(table, id, formData)
      : createEntity(table, formData);

    action
      .then(() => navigate("/admin/dashboard"))
      .catch((err) => {
        console.error("CRUD Error:", err);
        alert("There was an error. Check console.");
      })
      .finally(() => setLoading(false));
  };

  // === UI ===
  if (loading) return <p className="p-6">Loading...</p>;

  return (
    <div className="p-6 max-w-3xl mx-auto w-50">
      <h1 className="text-2xl font-bold mb-6">
        {isEdit ? "Edit" : "Create"} {table}
      </h1>

      <form onSubmit={handleSubmit} className="space-y-4">
        {Object.keys(formData).map((key) => (
          <div key={key}>
            <label className="block mb-1 capitalize">
              {key.replace(/_/g, " ")}
            </label>
            <input
              type={typeof formData[key] === "number" ? "number" : "text"}
              name={key}
              value={formData[key]}
              onChange={handleChange}
              className="form-control mb-3"
            />
          </div>
        ))}

        <div className="flex gap-4">
          <button type="submit" className="btn btn-outline-success">
            {isEdit ? "Update" : "Create"}
          </button>

          <button
            type="button"
            onClick={() => navigate(-1)}
            className="btn btn-outline-danger"
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default CrudForm;
