// src/pages/admin/Dashboard.jsx
import { useEffect, useState, useCallback } from "react";
import { getAdminDashboard, deleteEntity } from "../../services/adminApi";
import { useConfirm } from "../../hooks/useConfirm";
import CrudModal from "../../components/admin/CrudModal";
import "./Dashboard.css";

const EMPTY_LATEST = {
  users: [],
  categories: [],
  products: [],
  customers: [],
  orders: [],
  employees: [],
  sales: [],
  reservations: [],
};

const Dashboard = () => {
  const [stats, setStats] = useState({
    users: 0,
    categories: 0,
    products: 0,
    customers: 0,
    orders: 0,
    employees: 0,
    sales: 0,
    reservations: 0,
    revenue: 0,
  });

  const [latest, setLatest] = useState(EMPTY_LATEST);
  const [loading, setLoading] = useState(true);

  // CRUD Modal
  const [modalOpen, setModalOpen] = useState(false);
  const [currentTable, setCurrentTable] = useState("");
  const [formData, setFormData] = useState({});
  const { confirm, ConfirmModal } = useConfirm();

  // ÿ±ŸÖŸàÿ≤ ÿßŸÑÿ£ŸäŸÇŸàŸÜÿßÿ™ ŸÑŸÉŸÑ ŸÇÿ≥ŸÖ
  const iconMap = {
    users: "üë•",
    categories: "üìÅ",
    products: "üì¶",
    customers: "üë§",
    orders: "üõí",
    employees: "üíº",
    sales: "üìà",
    reservations: "üìÖ",
    revenue: "üí∞",
  };

  // =====================
  // Fetch Dashboard Data
  // =====================
  const fetchDashboard = useCallback(() => {
    setLoading(true);

    getAdminDashboard()
      .then((res) => {
        const data = res?.data || {};

        // ÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ•Ÿäÿ±ÿßÿØÿßÿ™
        const totalRevenue =
          (data.statistics?.orders || 0) * 100 +
          (data.statistics?.sales || 0) * 150; // ŸÖÿ´ÿßŸÑ ŸÑÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ•Ÿäÿ±ÿßÿØÿßÿ™

        // ---- Statistics
        setStats({
          users: data.statistics?.users ?? 0,
          categories: data.statistics?.categories ?? 0,
          products: data.statistics?.products ?? 0,
          customers: data.statistics?.customers ?? 0,
          orders: data.statistics?.orders ?? 0,
          employees: data.statistics?.employees ?? 0,
          sales: data.statistics?.sales ?? 0,
          reservations: data.statistics?.reservations ?? 0,
          revenue: totalRevenue,
        });

        // ---- Latest data
        setLatest({
          users: Array.isArray(data.latest?.users) ? data.latest.users : [],
          categories: Array.isArray(data.latest?.categories)
            ? data.latest.categories
            : [],
          products: Array.isArray(data.latest?.products)
            ? data.latest.products
            : [],
          customers: Array.isArray(data.latest?.customers)
            ? data.latest.customers
            : [],
          orders: Array.isArray(data.latest?.orders) ? data.latest.orders : [],
          employees: Array.isArray(data.latest?.employees)
            ? data.latest.employees
            : [],
          sales: Array.isArray(data.latest?.sales) ? data.latest.sales : [],
          reservations: Array.isArray(data.latest?.reservations)
            ? data.latest.reservations
            : [],
        });
      })
      .catch((err) => {
        console.error("Dashboard error:", err);
        setLatest(EMPTY_LATEST);
      })
      .finally(() => setLoading(false));
  }, []);

  useEffect(() => {
    fetchDashboard();
    // ÿ•ÿ∂ÿßŸÅÿ© class ŸÑŸÑbody ŸÑŸÑÿ™ÿµŸÖŸäŸÖ ÿßŸÑÿÆÿßÿµ
    document.body.classList.add("dashboard-page");
    return () => {
      document.body.classList.remove("dashboard-page");
    };
  }, [fetchDashboard]);

  // =====================
  // CRUD Actions
  // =====================
  const handleDelete = async (table, id) => {
    const ok = await confirm(
      "Delete Confirmation",
      `Are you sure you want to delete this ${table.slice(0, -1)}?`
    );

    if (!ok) return;

    deleteEntity(table, id).then(fetchDashboard).catch(console.error);
  };

  const handleAdd = (table) => {
    setCurrentTable(table);
    setFormData({});
    setModalOpen(true);
  };

  const handleEdit = (table, item) => {
    setCurrentTable(table);
    setFormData(item);
    setModalOpen(true);
  };

  const handleView = (table, item) => {
    console.log("View:", table, item);
    // ŸäŸÖŸÉŸÜ ÿ•ÿ∂ÿßŸÅÿ© redirect ÿ£Ÿà modal ŸáŸÜÿß
  };
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Submit:", currentTable, formData);
    setModalOpen(false);
  };

  if (loading)
    return (
      <div className="dashboard-loading">
        <div className="dashboard-loading-text">Loading Dashboard...</div>
      </div>
    );

  return (
    <div className="dashboard-container">
      {/* ================= Header ================= */}
      <div className="dashboard-header">
        <h1>Admin Dashboard</h1>
        <p>
          Welcome to your administration panel. Here you can manage all aspects
          of your application.
        </p>
      </div>

      {/* ================= Statistics ================= */}
      <div className="stats-grid">
        {Object.entries(stats).map(([key, value], index) => (
          <div key={key} className={`stat-card ${key}`}>
            <div className="stat-card-icon">{iconMap[key]}</div>
            <div className="stat-title">
              {key.charAt(0).toUpperCase() + key.slice(1)}
            </div>
            <div className="stat-value">
              {key === "revenue"
                ? `$${value.toLocaleString()}`
                : value.toLocaleString()}
            </div>
            <div
              className={`stat-change ${
                index % 3 === 0 ? "positive" : "positive"
              }`}
            >
              {index % 3 === 0 ? "+12.5%" : "+8.2%"} from last month
            </div>
          </div>
        ))}
      </div>

      {/* ================= Latest Tables ================= */}
      <div className="tables-grid">
        {Object.entries(latest).map(([table, data]) => {
          if (!Array.isArray(data)) return null;

          const columns =
            data.length > 0
              ? Object.keys(data[0])
                  .filter(
                    (key) =>
                      !["_id", "__v", "createdAt", "updatedAt"].includes(key)
                  )
                  .slice(0, 4) // ÿπÿ±ÿ∂ 4 ÿ£ÿπŸÖÿØÿ© ŸÅŸÇÿ∑
                  .map((key) => ({
                    key,
                    label: key
                      .replace(/_/g, " ")
                      .replace(/\b\w/g, (l) => l.toUpperCase()),
                  }))
              : [];

          return (
            <div key={table} className={`table-container ${table}`}>
              <div className="table-header">
                <h3>
                  {iconMap[table]} Latest{" "}
                  {table.charAt(0).toUpperCase() + table.slice(1)}
                  <span
                    style={{
                      fontSize: "0.9rem",
                      opacity: 0.9,
                      marginLeft: "0.5rem",
                    }}
                  >
                    ({data.length} items)
                  </span>
                </h3>
                <div className="table-actions">
                  <button
                    className="action-btn"
                    onClick={() => handleAdd(table)}
                  >
                    <span>+</span> Add New
                  </button>
                </div>
              </div>
              <div className="table-content">
                {data.length > 0 ? (
                  <table className="custom-table">
                    <thead>
                      <tr>
                        {columns.map((col) => (
                          <th key={col.key}>{col.label}</th>
                        ))}
                        <th>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {data.slice(0, 6).map((item, index) => (
                        <tr key={index}>
                          {columns.map((col) => (
                            <td key={col.key}>
                              {typeof item[col.key] === "object"
                                ? JSON.stringify(item[col.key]).substring(
                                    0,
                                    20
                                  ) + "..."
                                : String(item[col.key] || "-").substring(0, 30)}
                            </td>
                          ))}
                          <td className="table-actions-cell">
                            <button
                              className="icon-btn view"
                              onClick={() => handleView(table, item)}
                              title="View Details"
                            >
                              üëÅÔ∏è
                            </button>
                            <button
                              className="icon-btn edit"
                              onClick={() => handleEdit(table, item)}
                              title="Edit"
                            >
                              ‚úèÔ∏è
                            </button>
                            <button
                              className="icon-btn delete"
                              onClick={() =>
                                handleDelete(
                                  table,
                                  item.id || item._id || index
                                )
                              }
                              title="Delete"
                            >
                              üóëÔ∏è
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                ) : (
                  <div className="no-data">
                    <div className="no-data-icon">üìä</div>
                    <h4>No Data Available</h4>
                    <p>
                      No {table} records found. Click "Add New" to create your
                      first record.
                    </p>
                  </div>
                )}
              </div>
            </div>
          );
        })}
      </div>

      {/* ================= Confirm Modal ================= */}
      <ConfirmModal />
      <CrudModal
        isOpen={modalOpen}
        title={`${formData.id ? "Edit" : "Add"} ${currentTable}`}
        fields={Object.keys(formData)}
        formData={formData}
        onChange={handleChange}
        onSubmit={handleSubmit}
        onClose={() => setModalOpen(false)}
      />
    </div>
  );
};

export default Dashboard;
