  orders: ["id", "title", "description", "price", "quantity", "customer_id"],
  employees: ["id", "name", "email", "position", "salary"],
  sales: ["id", "title", "description", "price", "quantity", "employee_id"],
  reservations: ["id", "name", "email", "date", "time", "persons", "status"],
};

const Dashboard = () => {
  const [stats, setStats] = useState({
    users: 0,
    categories: 0,
    products: 0,
    customers: 0,
    orders: 0,
    employees: 0,
    sales: 0,
    reservations: 0,
    revenue: 0,
  });

  const [latest, setLatest] = useState(EMPTY_LATEST);
  const [loading, setLoading] = useState(true);

  // CRUD State
  const [modalOpen, setModalOpen] = useState(false);
  const [currentTable, setCurrentTable] = useState("");
  const [formData, setFormData] = useState({});

  // Toast Notifications
  const { toasts, addToast, ToastWrapper } = ToastContainer();
  const { confirm, ConfirmModal } = useConfirm();

  // ÿ±ŸÖŸàÿ≤ ÿßŸÑÿ£ŸäŸÇŸàŸÜÿßÿ™
  const iconMap = {
    users: "üë•",
    categories: "üìÅ",
    products: "üì¶",
    customers: "üë§",
    orders: "üõí",
    employees: "üíº",
    sales: "üìà",
    reservations: "üìÖ",
    revenue: "üí∞",
  };

  // =====================
  // Fetch Dashboard Data
  // =====================
  const fetchDashboard = useCallback(async () => {
    setLoading(true);
    try {
      const [statsRes, latestRes] = await Promise.all([
        adminApi.getStatistics(),
        Promise.all(
          Object.keys(EMPTY_LATEST).map((table) =>
            adminApi
              .getLatest(table, 5)
              .then((res) => ({ table, data: res.data }))
              .catch(() => ({ table, data: [] }))
          )
        ),
      ]);

      // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
      const statistics = statsRes.data || {};
      setStats({
        users: statistics.users || 0,
        categories: statistics.categories || 0,
        products: statistics.products || 0,
        customers: statistics.customers || 0,
        orders: statistics.orders || 0,
        employees: statistics.employees || 0,
        sales: statistics.sales || 0,
        reservations: statistics.reservations || 0,
        revenue: (statistics.orders || 0) * 100 + (statistics.sales || 0) * 150,
      });

      // ÿ™ÿ≠ÿØŸäÿ´ ÿ£ÿ≠ÿØÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      const newLatest = { ...EMPTY_LATEST };
      latestRes.forEach(({ table, data }) => {
        newLatest[table] = Array.isArray(data) ? data : [];
      });
      setLatest(newLatest);
    } catch (err) {
      console.error("Dashboard error:", err);
      addToast("ŸÅÿ¥ŸÑ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™", "error");
    } finally {
      setLoading(false);
    }
  }, [addToast]);

  useEffect(() => {
    fetchDashboard();
    document.body.classList.add("dashboard-page");
    return () => {
      document.body.classList.remove("dashboard-page");
    };
  }, [fetchDashboard]);

  // =====================
  // CRUD Operations
  // =====================
  const handleDelete = async (table, item) => {
    const ok = await confirm(
      "ÿ™ÿ£ŸÉŸäÿØ ÿßŸÑÿ≠ÿ∞ŸÅ",
      `ŸáŸÑ ÿ£ŸÜÿ™ ŸÖÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ≠ÿ∞ŸÅ ${table.slice(0, -1)}ÿü`
    );

    if (!ok) return;

    try {
      const id = item.id || item._id;
      await adminApi.delete(table, id);

      addToast(`ÿ™ŸÖ ÿ≠ÿ∞ŸÅ ${table.slice(0, -1)} ÿ®ŸÜÿ¨ÿßÿ≠`, "success");
      fetchDashboard(); // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
    } catch (err) {
      console.error("Delete error:", err);
      addToast("ŸÅÿ¥ŸÑ ÿπŸÖŸÑŸäÿ© ÿßŸÑÿ≠ÿ∞ŸÅ", "error");
    }
  };

  const handleAdd = (table) => {
    setCurrentTable(table);
    setFormData({}); // ÿ®ŸäÿßŸÜÿßÿ™ ŸÅÿßÿ±ÿ∫ÿ© ŸÑŸÑÿ•ÿ∂ÿßŸÅÿ©
    setModalOpen(true);
  };

  const handleEdit = (table, item) => {
    setCurrentTable(table);
    setFormData({ ...item }); // ŸÜÿ≥ÿÆ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÑŸÑÿ™ÿπÿØŸäŸÑ
    setModalOpen(true);
  };

  const handleView = (table, item) => {
    // ŸäŸÖŸÉŸÜ ŸÅÿ™ÿ≠ modal ŸÑÿπÿ±ÿ∂ ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ
    console.log("View:", table, item);
    addToast(`ÿπÿ±ÿ∂ ÿ™ŸÅÿßÿµŸäŸÑ ${table.slice(0, -1)}`, "info");
  };

  const handleModalSubmit = async (data) => {
    try {
      const isEdit = !!data.id;

      if (isEdit) {
        await adminApi.update(currentTable, data.id, data);
        addToast(`ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿ´ ${currentTable.slice(0, -1)} ÿ®ŸÜÿ¨ÿßÿ≠`, "success");
      } else {
        await adminApi.create(currentTable, data);
        addToast(`ÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ${currentTable.slice(0, -1)} ÿ¨ÿØŸäÿØ`, "success");
      }

      setModalOpen(false);
      fetchDashboard(); // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
    } catch (err) {
      console.error("Submit error:", err);
      addToast("ŸÅÿ¥ŸÑ ÿπŸÖŸÑŸäÿ© ÿßŸÑÿ≠ŸÅÿ∏", "error");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // =====================
  // Render Helpers
  // =====================
  const renderTableColumns = (table, data) => {
    if (!data.length) return [];

    // ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑÿ≠ŸÇŸàŸÑ ÿßŸÑŸÖÿ≠ÿØÿØÿ© ÿ£Ÿà ÿßÿ≥ÿ™ÿÆÿ±ÿßÿ¨Ÿáÿß ŸÖŸÜ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
    const fields =
      TABLE_FIELDS[table] ||
      Object.keys(data[0])
        .filter(
          (key) => !["_id", "__v", "createdAt", "updatedAt"].includes(key)
        )
        .slice(0, 4);

    return fields.map((key) => ({
      key,
      label: key.replace(/_/g, " ").replace(/\b\w/g, (l) => l.toUpperCase()),
    }));
  };

  const formatCellValue = (value) => {
    if (value === null || value === undefined) return "-";
    if (typeof value === "object") {
      return JSON.stringify(value).substring(0, 20) + "...";
    }
    return String(value).substring(0, 30);
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="dashboard-loading-text">ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...</div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      {/* Toast Notifications */}
      <ToastWrapper />

      {/* ================= Header ================= */}
      <div className="dashboard-header">
        <h1>ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ ÿßŸÑÿ•ÿØÿßÿ±Ÿäÿ©</h1>
        <p>ŸÖÿ±ÿ≠ÿ®ÿßŸã ÿ®ŸÉ ŸÅŸä ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ. ŸäŸÖŸÉŸÜŸÉ ÿ•ÿØÿßÿ±ÿ© ÿ¨ŸÖŸäÿπ ÿ¨ŸàÿßŸÜÿ® ÿ™ÿ∑ÿ®ŸäŸÇŸÉ ŸÖŸÜ ŸáŸÜÿß.</p>
      </div>

      {/* ================= Statistics ================= */}
      <div className="stats-grid">
        {Object.entries(stats).map(([key, value], index) => (
          <div key={key} className={`stat-card ${key}`}>
            <div className="stat-card-icon">{iconMap[key]}</div>
            <div className="stat-title">
              {key === "revenue"
                ? "ÿßŸÑÿ•Ÿäÿ±ÿßÿØÿßÿ™"
                : key === "users"
                ? "ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ"
                : key === "categories"
                ? "ÿßŸÑŸÅÿ¶ÿßÿ™"
                : key === "products"
                ? "ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™"
                : key === "customers"
                ? "ÿßŸÑÿπŸÖŸÑÿßÿ°"
                : key === "orders"
                ? "ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™"
                : key === "employees"
                ? "ÿßŸÑŸÖŸàÿ∏ŸÅŸäŸÜ"
                : key === "sales"
                ? "ÿßŸÑŸÖÿ®Ÿäÿπÿßÿ™"
                : key === "reservations"
                ? "ÿßŸÑÿ≠ÿ¨Ÿàÿ≤ÿßÿ™"
                : key}
            </div>
            <div className="stat-value">
              {key === "revenue"
                ? `$${value.toLocaleString()}`
                : value.toLocaleString()}
            </div>
            <div className={`stat-change positive`}>
              {index % 3 === 0 ? "+12.5%" : "+8.2%"} ŸÖŸÜ ÿßŸÑÿ¥Ÿáÿ± ÿßŸÑŸÖÿßÿ∂Ÿä
            </div>
          </div>
        ))}
      </div>

      {/* ================= Latest Tables ================= */}
      <div className="tables-grid">
        {Object.entries(latest).map(([table, data]) => {
          const columns = renderTableColumns(table, data);

          return (
            <div key={table} className={`table-container ${table}`}>
              <div className="table-header">
                <h3>
                  {iconMap[table]} ÿ£ÿ≠ÿØÿ´{" "}
                  {table === "users"
                    ? "ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ"
                    : table === "categories"
                    ? "ÿßŸÑŸÅÿ¶ÿßÿ™"
                    : table === "products"
                    ? "ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™"
                    : table === "customers"
                    ? "ÿßŸÑÿπŸÖŸÑÿßÿ°"
                    : table === "orders"
                    ? "ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™"
                    : table === "employees"
                    ? "ÿßŸÑŸÖŸàÿ∏ŸÅŸäŸÜ"
                    : table === "sales"
                    ? "ÿßŸÑŸÖÿ®Ÿäÿπÿßÿ™"
                    : table === "reservations"
                    ? "ÿßŸÑÿ≠ÿ¨Ÿàÿ≤ÿßÿ™"
                    : table}
                  <span className="table-count">({data.length} ÿπŸÜÿµÿ±)</span>
                </h3>
                <div className="table-actions">
                  <button
                    className="action-btn"
                    onClick={() => handleAdd(table)}
                  >
                    <span>+</span> ÿ•ÿ∂ÿßŸÅÿ© ÿ¨ÿØŸäÿØ
                  </button>
                </div>
              </div>
              <div className="table-content">
                {data.length > 0 ? (
                  <table className="custom-table">
                    <thead>
                      <tr>
                        {columns.map((col) => (
                          <th key={col.key}>{col.label}</th>
                        ))}
                        <th>ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™</th>
                      </tr>
                    </thead>
                    <tbody>
                      {data.slice(0, 6).map((item, index) => (
                        <tr key={index}>
                          {columns.map((col) => (
                            <td key={col.key} title={item[col.key]}>
                              {formatCellValue(item[col.key])}
                            </td>
                          ))}
                          <td className="table-actions-cell">
                            <button
                              className="icon-btn view"
                              onClick={() => handleView(table, item)}
                              title="ÿπÿ±ÿ∂ ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ"
                            >
                              üëÅÔ∏è
                            </button>
                            <button
                              className="icon-btn edit"
                              onClick={() => handleEdit(table, item)}
                              title="ÿ™ÿπÿØŸäŸÑ"
                            >
                              ‚úèÔ∏è
                            </button>
                            <button
                              className="icon-btn delete"
                              onClick={() => handleDelete(table, item)}
                              title="ÿ≠ÿ∞ŸÅ"
                            >
                              üóëÔ∏è
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                ) : (
                  <div className="no-data">
                    <div className="no-data-icon">üìä</div>
                    <h4>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ®ŸäÿßŸÜÿßÿ™</h4>
                    <p>
                      ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ {table} ÿ≥ÿ¨ŸÑÿßÿ™. ÿßŸÜŸÇÿ± ÿπŸÑŸâ "ÿ•ÿ∂ÿßŸÅÿ© ÿ¨ÿØŸäÿØ"
                      ŸÑÿ•ŸÜÿ¥ÿßÿ° ÿ£ŸàŸÑ ÿ≥ÿ¨ŸÑ.
                    </p>
                  </div>
                )}
              </div>
            </div>
          );
        })}
      </div>

      {/* ================= Modals ================= */}
      <ConfirmModal />

      <CrudModal
        isOpen={modalOpen}
        title={`${formData.id ? "ÿ™ÿπÿØŸäŸÑ" : "ÿ•ÿ∂ÿßŸÅÿ©"} ${currentTable}`}
        fields={TABLE_FIELDS[currentTable] || Object.keys(formData)}
        formData={formData}
        onChange={handleChange}
        onSubmit={handleModalSubmit}
        onClose={() => setModalOpen(false)}
      />
    </div>
  );
};

export default Dashboard;
