// src/components/admin/LatestTable.jsx
const LatestTable = ({ title, data, columns, onAdd, onEdit, onDelete }) => {
  return (
    <div className="table-container">
      <div className="table-header">
        <h3>{title}</h3>
        <div className="table-actions">
          <button className="action-btn" onClick={onAdd}>
            + Add New
          </button>
        </div>
      </div>
      <div className="table-content">
        {data.length > 0 ? (
          <table className="custom-table">
            <thead>
              <tr>
                {columns.slice(0, 3).map((col) => (
                  <th key={col.key}>{col.label}</th>
                ))}
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {data.slice(0, 5).map((item, index) => (
                <tr key={index}>
                  {columns.slice(0, 3).map((col) => (
                    <td key={col.key}>
                      {typeof item[col.key] === "object"
                        ? JSON.stringify(item[col.key]).substring(0, 30) + "..."
                        : String(item[col.key]).substring(0, 50)}
                    </td>
                  ))}
                  <td className="table-actions-cell">
                    <button
                      className="icon-btn edit"
                      onClick={() => onEdit(item)}
                      title="Edit"
                    >
                      ‚úèÔ∏è
                    </button>
                    <button
                      className="icon-btn delete"
                      onClick={() => onDelete(item.id || item._id || index)}
                      title="Delete"
                    >
                      üóëÔ∏è
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <div className="no-data">
            <div className="no-data-icon">üìä</div>
            <p>No data available</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default LatestTable;
