// src/services/adminApi.js - النسخة المصححة
import axios from "axios";

const API = axios.create({
  baseURL: "http://127.0.0.1:8000/api",
});

API.interceptors.request.use((config) => {
  const token = localStorage.getItem("token");
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Interceptor لمعالجة الأخطاء
API.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem("token");
      window.location.href = "/login";
    }
    return Promise.reject(error);
  }
);

// ===== Dashboard API =====
export const getAdminDashboard = () => API.get("/admin/dashboard");

// ===== Users =====
export const getUsers = () => axios.get("/users");
export const createUser = (data) => axios.post("/users", data);
export const updateUser = (id, data) => axios.put(`/users/${id}`, data);
export const deleteUser = (id) => axios.delete(`/users/${id}`);

// ===== Categories =====
export const getCategories = () => axios.get("/categories");
export const createCategory = (data) => axios.post("/categories", data);
export const updateCategory = (id, data) =>
  axios.put(`/categories/${id}`, data);
export const deleteCategory = (id) => axios.delete(`/categories/${id}`);

// ===== Products =====
export const getProducts = () => axios.get("/products");
export const createProduct = (data) => axios.post("/products", data);
export const updateProduct = (id, data) => axios.put(`/products/${id}`, data);
export const deleteProduct = (id) => axios.delete(`/products/${id}`);

// ===== Customers =====
export const getCustomers = () => axios.get("/customers");
export const createCustomer = (data) => axios.post("/customers", data);
export const updateCustomer = (id, data) => axios.put(`/customers/${id}`, data);
export const deleteCustomer = (id) => axios.delete(`/customers/${id}`);

// ===== Orders =====
export const getOrders = () => axios.get("/orders");
export const createOrder = (data) => axios.post("/orders", data);
export const updateOrder = (id, data) => axios.put(`/orders/${id}`, data);
export const deleteOrder = (id) => axios.delete(`/orders/${id}`);

// ===== Employees =====
export const getEmployees = () => axios.get("/employees");
export const createEmployee = (data) => axios.post("/employees", data);
export const updateEmployee = (id, data) => axios.put(`/employees/${id}`, data);
export const deleteEmployee = (id) => axios.delete(`/employees/${id}`);

// ===== Sales =====
export const getSales = () => axios.get("/sales");
export const createSale = (data) => axios.post("/sales", data);
export const updateSale = (id, data) => axios.put(`/sales/${id}`, data);
export const deleteSale = (id) => axios.delete(`/sales/${id}`);

// ===== Reservations =====
export const getReservations = () => axios.get("/reservations");
export const createReservation = (data) => axios.post("/reservations", data);
export const updateReservation = (id, data) =>
  axios.put(`/reservations/${id}`, data);
export const deleteReservation = (id) => axios.delete(`/reservations/${id}`);
