import React from "react";

const LatestTable = ({
  title,
  data = [],
  columns = [],
  onEdit,
  onDelete,
  onAdd,
}) => {
  return (
    <div className="bg-white shadow rounded p-4">
      <div className="flex justify-between items-center mb-2">
        <h2 className="font-bold text-lg">{title}</h2>
        {onAdd && (
          <button
            onClick={onAdd}
            className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
          >
            Add New
          </button>
        )}
      </div>

      <table className="w-full table-auto border-collapse border border-gray-200">
        <thead className="bg-gray-100">
          <tr>
            {columns.map((col) => (
              <th
                key={col.key}
                className="border border-gray-200 px-2 py-1 text-left"
              >
                {col.label}
              </th>
            ))}
            {(onEdit || onDelete) && (
              <th className="border border-gray-200 px-2 py-1">Actions</th>
            )}
          </tr>
        </thead>
        <tbody>
          {data.length === 0 ? (
            <tr>
              <td colSpan={columns.length + 1} className="text-center py-2">
                No data found
              </td>
            </tr>
          ) : (
            data.map((item) => (
              <tr key={item.id}>
                {columns.map((col) => (
                  <td
                    key={col.key}
                    className="border border-gray-200 px-2 py-1"
                  >
                    {item[col.key]}
                  </td>
                ))}
                {(onEdit || onDelete) && (
                  <td className="border border-gray-200 px-2 py-1 space-x-2">
                    {onEdit && (
                      <button
                        onClick={() => onEdit(item)}
                        className="bg-yellow-400 px-2 py-1 rounded hover:bg-yellow-500 text-white"
                      >
                        Edit
                      </button>
                    )}
                    {onDelete && (
                      <button
                        onClick={() => onDelete(item.id)}
                        className="bg-red-500 px-2 py-1 rounded hover:bg-red-600 text-white"
                      >
                        Delete
                      </button>
                    )}
                  </td>
                )}
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default LatestTable;
