// src/pages/admin/Dashboard.jsx - ÿßŸÑÿ¨ÿ≤ÿ° ÿßŸÑŸÖÿµÿ≠ÿ≠
import { useEffect, useState, useCallback } from "react";
import {
  getAdminDashboard,
  usersApi,
  categoriesApi,
  productsApi,
  customersApi,
  ordersApi,
  employeesApi,
  salesApi,
  reservationsApi,
  entityApi,
} from "../../services/adminApi";
import { useConfirm } from "../../hooks/useConfirm";
import CrudModal from "../../components/admin/CrudModal";
import ToastContainer from "../../components/admin/ToastContainer";
import "./Dashboard.css";

const EMPTY_LATEST = {
  users: [],
  categories: [],
  products: [],
  customers: [],
  orders: [],
  employees: [],
  sales: [],
  reservations: [],
};

// ÿ™ÿπÿ±ŸäŸÅ APIs ŸÑŸÉŸÑ ŸÉŸäÿßŸÜ
const ENTITY_APIS = {
  users: usersApi,
  categories: categoriesApi,
  products: productsApi,
  customers: customersApi,
  orders: ordersApi,
  employees: employeesApi,
  sales: salesApi,
  reservations: reservationsApi,
};

const Dashboard = () => {
  const [stats, setStats] = useState({
    users: 0,
    categories: 0,
    products: 0,
    customers: 0,
    orders: 0,
    employees: 0,
    sales: 0,
    reservations: 0,
    revenue: 0,
  });

  const [latest, setLatest] = useState(EMPTY_LATEST);
  const [loading, setLoading] = useState(true);

  // CRUD State
  const [modalOpen, setModalOpen] = useState(false);
  const [currentTable, setCurrentTable] = useState("");
  const [formData, setFormData] = useState({});

  // Toast Notifications
  const { toasts, addToast, ToastWrapper } = ToastContainer();
  const { confirm, ConfirmModal } = useConfirm();

  // =====================
  // Fetch Dashboard Data - ÿßŸÑŸÖÿµÿ≠ÿ≠
  // =====================
  const fetchDashboard = useCallback(async () => {
    setLoading(true);
    try {
      // 1. ÿ¨ŸÑÿ® ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿØÿßÿ¥ÿ®Ÿàÿ±ÿØ (ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™)
      const dashboardRes = await getAdminDashboard();
      const data = dashboardRes?.data || {};

      // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
      setStats({
        users: data.statistics?.users || 0,
        categories: data.statistics?.categories || 0,
        products: data.statistics?.products || 0,
        customers: data.statistics?.customers || 0,
        orders: data.statistics?.orders || 0,
        employees: data.statistics?.employees || 0,
        sales: data.statistics?.sales || 0,
        reservations: data.statistics?.reservations || 0,
        revenue:
          (data.statistics?.orders || 0) * 100 +
          (data.statistics?.sales || 0) * 150,
      });

      // 2. ÿ¨ŸÑÿ® ÿ£ÿ≠ÿØÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ - ÿßŸÑÿ∑ÿ±ŸäŸÇÿ© ÿßŸÑÿ¢ŸÖŸÜÿ©
      const latestPromises = Object.keys(ENTITY_APIS).map(async (entity) => {
        try {
          const api = ENTITY_APIS[entity];

          // ŸÖÿ≠ÿßŸàŸÑÿ© ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ getLatest ÿ•ÿ∞ÿß ŸÉÿßŸÜ ŸÖŸàÿ¨ŸàÿØÿßŸã
          if (api.getLatest) {
            const res = await api.getLatest(5);
            return { entity, data: res.data };
          }
          // ÿ£Ÿà ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ get ÿßŸÑÿπÿßÿØŸä Ÿàÿ™ÿ±ÿ™Ÿäÿ® ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸäÿØŸàŸäÿßŸã
          else if (api.get) {
            const res = await api.get();
            const data = Array.isArray(res.data) ? res.data : [];
            // ÿ£ÿÆÿ∞ ÿ¢ÿÆÿ± 5 ÿπŸÜÿßÿµÿ±
            const latestData = data.slice(-5).reverse();
            return { entity, data: latestData };
          }
        } catch (err) {
          console.warn(`Failed to fetch ${entity}:`, err);
          // ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ entityApi ŸÉÿ®ÿØŸäŸÑ
          try {
            const res = await entityApi.getLatest(entity, 5);
            return { entity, data: res.data };
          } catch (e) {
            console.warn(`Alternative fetch failed for ${entity}:`, e);
          }
        }
        return { entity, data: [] };
      });

      const latestResults = await Promise.all(latestPromises);
      const newLatest = { ...EMPTY_LATEST };

      latestResults.forEach(({ entity, data }) => {
        newLatest[entity] = Array.isArray(data) ? data.slice(0, 5) : [];
      });

      setLatest(newLatest);
    } catch (err) {
      console.error("Dashboard error:", err);
      addToast("ŸÅÿ¥ŸÑ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™", "error");
    } finally {
      setLoading(false);
    }
  }, [addToast]);

  // =====================
  // CRUD Operations - ŸÖÿµÿ≠ÿ≠
  // =====================
  const handleDelete = async (table, item) => {
    const itemName =
      table === "users"
        ? "ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ"
        : table === "categories"
        ? "ÿßŸÑŸÅÿ¶ÿ©"
        : table === "products"
        ? "ÿßŸÑŸÖŸÜÿ™ÿ¨"
        : table === "customers"
        ? "ÿßŸÑÿπŸÖŸäŸÑ"
        : table === "orders"
        ? "ÿßŸÑÿ∑ŸÑÿ®"
        : table === "employees"
        ? "ÿßŸÑŸÖŸàÿ∏ŸÅ"
        : table === "sales"
        ? "ÿßŸÑŸÖÿ®Ÿäÿπ"
        : "ÿßŸÑÿ≠ÿ¨ÿ≤";

    const ok = await confirm("ÿ™ÿ£ŸÉŸäÿØ ÿßŸÑÿ≠ÿ∞ŸÅ", `ŸáŸÑ ÿ£ŸÜÿ™ ŸÖÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ≠ÿ∞ŸÅ ${itemName}ÿü`);

    if (!ok) return;

    try {
      const id = item.id || item._id;
      const api = ENTITY_APIS[table];

      if (api && api.delete) {
        await api.delete(id);
      } else {
        await entityApi.delete(table, id);
      }

      addToast(`ÿ™ŸÖ ÿ≠ÿ∞ŸÅ ${itemName} ÿ®ŸÜÿ¨ÿßÿ≠`, "success");
      fetchDashboard(); // ÿ•ÿπÿßÿØÿ© ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
    } catch (err) {
      console.error("Delete error:", err);
      addToast("ŸÅÿ¥ŸÑ ÿπŸÖŸÑŸäÿ© ÿßŸÑÿ≠ÿ∞ŸÅ", "error");
    }
  };

  const handleModalSubmit = async (data) => {
    try {
      const api = ENTITY_APIS[currentTable];
      const isEdit = !!data.id;

      if (isEdit) {
        // ÿ™ÿ≠ÿØŸäÿ´
        if (api && api.update) {
          await api.update(data.id, data);
        } else {
          await entityApi.update(currentTable, data.id, data);
        }
        addToast(`ÿ™ŸÖ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ ÿ®ŸÜÿ¨ÿßÿ≠`, "success");
      } else {
        // ÿ•ÿ∂ÿßŸÅÿ©
        if (api && api.create) {
          await api.create(data);
        } else {
          await entityApi.create(currentTable, data);
        }
        addToast(`ÿ™ŸÖ ÿßŸÑÿ•ŸÜÿ¥ÿßÿ° ÿ®ŸÜÿ¨ÿßÿ≠`, "success");
      }

      setModalOpen(false);
      fetchDashboard(); // ÿ•ÿπÿßÿØÿ© ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
    } catch (err) {
      console.error("Submit error:", err);
      addToast("ŸÅÿ¥ŸÑ ÿπŸÖŸÑŸäÿ© ÿßŸÑÿ≠ŸÅÿ∏", "error");
    }
  };

  // ... ÿ®ÿßŸÇŸä ÿßŸÑŸÉŸàÿØ Ÿäÿ®ŸÇŸâ ŸÉŸÖÿß ŸáŸà

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // =====================
  // Render Helpers
  // =====================
  const renderTableColumns = (table, data) => {
    if (!data.length) return [];

    const fields =
      TABLE_FIELDS[table] ||
      Object.keys(data[0])
        .filter(
          (key) => !["_id", "__v", "createdAt", "updatedAt", "id"].includes(key)
        )
        .slice(0, 4);

    return fields.map((key) => ({
      key,
      label: key.replace(/_/g, " ").replace(/\b\w/g, (l) => l.toUpperCase()),
    }));
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="dashboard-loading-text">ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...</div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      {/* Toast Notifications */}
      <ToastWrapper />

      {/* ================= Header ================= */}
      <div className="dashboard-header">
        <h1>ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ ÿßŸÑÿ•ÿØÿßÿ±Ÿäÿ©</h1>
        <p>ŸÖÿ±ÿ≠ÿ®ÿßŸã ÿ®ŸÉ ŸÅŸä ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ. ŸäŸÖŸÉŸÜŸÉ ÿ•ÿØÿßÿ±ÿ© ÿ¨ŸÖŸäÿπ ÿ¨ŸàÿßŸÜÿ® ÿ™ÿ∑ÿ®ŸäŸÇŸÉ ŸÖŸÜ ŸáŸÜÿß.</p>
      </div>

      {/* ================= Statistics ================= */}
      <div className="stats-grid">
        {Object.entries(stats).map(([key, value], index) => (
          <div key={key} className={`stat-card ${key}`}>
            <div className="stat-card-icon">{iconMap[key]}</div>
            <div className="stat-title">
              {key === "revenue"
                ? "ÿßŸÑÿ•Ÿäÿ±ÿßÿØÿßÿ™"
                : key === "users"
                ? "ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ"
                : key === "categories"
                ? "ÿßŸÑŸÅÿ¶ÿßÿ™"
                : key === "products"
                ? "ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™"
                : key === "customers"
                ? "ÿßŸÑÿπŸÖŸÑÿßÿ°"
                : key === "orders"
                ? "ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™"
                : key === "employees"
                ? "ÿßŸÑŸÖŸàÿ∏ŸÅŸäŸÜ"
                : key === "sales"
                ? "ÿßŸÑŸÖÿ®Ÿäÿπÿßÿ™"
                : key === "reservations"
                ? "ÿßŸÑÿ≠ÿ¨Ÿàÿ≤ÿßÿ™"
                : key}
            </div>
            <div className="stat-value">
              {key === "revenue"
                ? `$${value.toLocaleString()}`
                : value.toLocaleString()}
            </div>
            <div className={`stat-change positive`}>
              {index % 3 === 0 ? "+12.5%" : "+8.2%"} ŸÖŸÜ ÿßŸÑÿ¥Ÿáÿ± ÿßŸÑŸÖÿßÿ∂Ÿä
            </div>
          </div>
        ))}
      </div>

      {/* ================= Latest Tables ================= */}
      <div className="tables-grid">
        {Object.entries(latest).map(([table, data]) => {
          const columns = renderTableColumns(table, data);

          return (
            <div key={table} className={`table-container ${table}`}>
              <div className="table-header">
                <h3>
                  {iconMap[table]} ÿ£ÿ≠ÿØÿ´{" "}
                  {table === "users"
                    ? "ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ"
                    : table === "categories"
                    ? "ÿßŸÑŸÅÿ¶ÿßÿ™"
                    : table === "products"
                    ? "ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™"
                    : table === "customers"
                    ? "ÿßŸÑÿπŸÖŸÑÿßÿ°"
                    : table === "orders"
                    ? "ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™"
                    : table === "employees"
                    ? "ÿßŸÑŸÖŸàÿ∏ŸÅŸäŸÜ"
                    : table === "sales"
                    ? "ÿßŸÑŸÖÿ®Ÿäÿπÿßÿ™"
                    : table === "reservations"
                    ? "ÿßŸÑÿ≠ÿ¨Ÿàÿ≤ÿßÿ™"
                    : table}
                  <span className="table-count">({data.length} ÿπŸÜÿµÿ±)</span>
                </h3>
                <div className="table-actions">
                  <button
                    className="action-btn"
                    onClick={() => handleAdd(table)}
                  >
                    <span>+</span> ÿ•ÿ∂ÿßŸÅÿ© ÿ¨ÿØŸäÿØ
                  </button>
                </div>
              </div>
              <div className="table-content">
                {data.length > 0 ? (
                  <table className="custom-table">
                    <thead>
                      <tr>
                        {columns.map((col) => (
                          <th key={col.key}>{col.label}</th>
                        ))}
                        <th>ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™</th>
                      </tr>
                    </thead>
                    <tbody>
                      {data.slice(0, 6).map((item, index) => (
                        <tr key={index}>
                          {columns.map((col) => (
                            <td key={col.key} title={item[col.key]}>
                              {item[col.key]?.toString().substring(0, 30) ||
                                "-"}
                            </td>
                          ))}
                          <td className="table-actions-cell">
                            <button
                              className="icon-btn edit"
                              onClick={() => handleEdit(table, item)}
                              title="ÿ™ÿπÿØŸäŸÑ"
                            >
                              ‚úèÔ∏è
                            </button>
                            <button
                              className="icon-btn delete"
                              onClick={() => handleDelete(table, item)}
                              title="ÿ≠ÿ∞ŸÅ"
                            >
                              üóëÔ∏è
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                ) : (
                  <div className="no-data">
                    <div className="no-data-icon">üìä</div>
                    <h4>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ®ŸäÿßŸÜÿßÿ™</h4>
                    <p>ÿßŸÜŸÇÿ± ÿπŸÑŸâ "ÿ•ÿ∂ÿßŸÅÿ© ÿ¨ÿØŸäÿØ" ŸÑÿ•ŸÜÿ¥ÿßÿ° ÿ£ŸàŸÑ ÿ≥ÿ¨ŸÑ.</p>
                  </div>
                )}
              </div>
            </div>
          );
        })}
      </div>

      {/* ================= Modals ================= */}
      <ConfirmModal />

      <CrudModal
        isOpen={modalOpen}
        title={`${formData.id ? "ÿ™ÿπÿØŸäŸÑ" : "ÿ•ÿ∂ÿßŸÅÿ©"} ${
          currentTable === "users"
            ? "ŸÖÿ≥ÿ™ÿÆÿØŸÖ"
            : currentTable === "categories"
            ? "ŸÅÿ¶ÿ©"
            : currentTable === "products"
            ? "ŸÖŸÜÿ™ÿ¨"
            : currentTable === "customers"
            ? "ÿπŸÖŸäŸÑ"
            : currentTable === "orders"
            ? "ÿ∑ŸÑÿ®"
            : currentTable === "employees"
            ? "ŸÖŸàÿ∏ŸÅ"
            : currentTable === "sales"
            ? "ŸÖÿ®Ÿäÿπ"
            : "ÿ≠ÿ¨ÿ≤"
        }`}
        fields={TABLE_FIELDS[currentTable] || Object.keys(formData)}
        formData={formData}
        onChange={handleChange}
        onSubmit={handleModalSubmit}
        onClose={() => setModalOpen(false)}
      />
    </div>
  );
};

export default Dashboard;
